const slider=document.querySelector(".slider"),prevButton=document.querySelector(".prev-butt"),nextButton=document.querySelector(".next-butt"),slides=Array.from(slider.querySelectorAll("img")),slideCount=slides.length;let slideIndex=0;prevButton.addEventListener("click",(()=>{slideIndex=(slideIndex-1+slideCount)%slideCount,slide()})),nextButton.addEventListener("click",(()=>{slideIndex=(slideIndex+1)%slideCount,slide()}));const slide=()=>{const e=slider.clientWidth,t=-slideIndex*e;slider.style.transform=`translateX(${t}px)`};window.addEventListener("load",(()=>{slide()}));